<script>

export default {
    mounted: function() {
        this.after_style = "visibility:hidden;";
        this.title_first = document.getElementById("svg-title-first");
        this.title_last = document.getElementById("svg-title-last").getElementsByTagName('span')[0];
        document.documentElement.style.setProperty('--main-animation-duration', this.total_time + "s");
        this.order = [
            {name: 'rpi', desc: ['IoT', 'Maker']},
            {name: 'lgbt', desc: ['LGBT', 'Advocate']},
            {name: 'collie', desc: ['Dog', 'Communicator']},
            {name: 'ai', desc: ['AI', 'Researcher']},
            {name: 'raccoon', desc: ['Raccoon', 'Apologist']}
        ]
        this.looping();
    },
    props: ['total_time'],
    watch: {

    },
    methods: {
        looping: function() {
            var i = 0;
            var ob = this.order[i];
            this.animate(ob.name);
            this.renderDescriptor(ob.desc);
            i += 1;
            setInterval(() => {
                ob = this.order[i];
                this.animate(ob.name);
                this.renderDescriptor(ob.desc);
                i = (i + 1) % this.order.length;
            }, this.total_time * 1000);
        },
        animate: function(icon) {
            var svg_str = "svg-" + icon;
            var svg = document.getElementById(svg_str);
                svg.classList.add(icon);

            svg.addEventListener("animationend", () => {
                svg.classList.remove(icon);
                svg.style = this.after_style;
            }, false);
        },
        renderDescriptor: function(desc) {
            document.documentElement.style.setProperty('--main-em-size', ((desc[0].length/2) + 1) + "em");
            this.title_first.innerHTML = desc[0];
            // document.documentElement.style.setProperty('--main-em-last-size', desc[1].length + "em");
            this.title_last.innerHTML = desc[1];
        }
    }
}

</script>

<style>
